/**
 * RIGHT SIDEBAR
 */

function openNav() {
    if (document.body.clientWidth > 600) {
        var width = "250px";
    } else {
        var width = "100%";
    }
    document.getElementById("rightsidebar").style.width = width;
    document.getElementById("dashboard-wrapper").style.marginRight = width;
}

function closeNav() {
    document.getElementById("rightsidebar").style.width = "0";
    document.getElementById("dashboard-wrapper").style.marginRight = "0px";
}

function openNavRight() {
    if (document.body.clientWidth > 600) {
        var width = "250px";
    } else {
        var width = "100%";
    }
    document.getElementById("SidebarRight").style.width = width;
    document.getElementById("MainContent").style.marginRight = width;
}

function closeNavRight() {
    document.getElementById("SidebarRight").style.width = "0";
    document.getElementById("MainContent").style.marginRight = "0px";
}

/**
 * LEFT SIDEBAR
 */

function openNavLeft() {
    document.getElementById("icon-btn-filter").innerText = ">";
    var SidenavLeft = document.getElementById("SidenavLeft");
    var MainContent = document.getElementById("MainContent");
    if (SidenavLeft.offsetWidth >= 10) {
        closeNavLeft();
    } else {
        if (document.body.clientWidth > 600) {
            var width = "250px";
            SidenavLeft.style.width = width;
            MainContent.style.marginLeft = width;
        } else {
            var width = "50%";
            SidenavLeft.style.width = width;
        }
    }

}

function closeNavLeft() {
    document.getElementById("SidenavLeft").style.width = "0";
    document.getElementById("SidenavLeft2").style.width = "0";
    document.getElementById("MainContent").style.marginLeft = "0";
}


/**
 * LEFT 2 SIDEBAR
 */

function openNavLeft2() {
    if (document.body.clientWidth > 600) {
        var width = "250px";
    } else {
        var width = "50%";
    }
    var icon = document.getElementById("icon-btn-filter");
    var SidenavLeft2 = document.getElementById("SidenavLeft2");
    getWidth = SidenavLeft2.offsetWidth;

    if (getWidth >= 10) {
        icon.innerText = ">";
        closeNavLeft2();
    } else {
        icon.innerText = "<";
        SidenavLeft2.style.left = width;
        SidenavLeft2.style.width = width;
    }
}

function closeNavLeft2() {
    document.getElementById("SidenavLeft2").style.width = "0";
}


window.addEventListener("resize", resizeScreen);
function resizeScreen() {
    if (document.body.clientWidth > 600) {
        var width = "250px";
    } else {
        var width = 0;
    }
    document.getElementById("SidenavLeft").style.width = width;
    document.getElementById("MainContent").style.marginLeft = width;
    document.getElementById("SidenavLeft2").style.width = 0;
    document.getElementById("icon-btn-filter").innerText = ">";

}


function capitalizeFirstLetter(str) {
    return str[0].toUpperCase() + str.slice(1);
}

/**
 * alert
 */

Livewire.on('toastAlert', value => {
    toastAlert(value['icon'], value['text']);
})


function toastAlert(icon, message) {
    const Toast = Swal.mixin({
        toast: true,
        position: 'bottom-end',
        showConfirmButton: false,
        timer: 6000,
        timerProgressBar: true,
        didOpen: (toast) => {
            toast.addEventListener('mouseenter', Swal.stopTimer)
            toast.addEventListener('mouseleave', Swal.resumeTimer)
        }
    })

    Toast.fire({
        icon: icon,
        title: message
    })
}


